Cypress.Commands.add('checkBoxValue', (checkboxValues, albumName, searchForName) => {
  cy.get('.search__form input').type(searchForName);
  cy.get(checkboxValues).check();
  cy.get('.album__name').contains(albumName);
  cy.screenshot();
  cy.get(checkboxValues).uncheck();
  cy.get('.search__form input').clear();
})
